mapper.select:
SELECT u.name,u.id,u.email
FROM users u
WHERE u.seq = #{seq}

SELECT DISTINCT t.*
FROM team t
LEFT OUTER JOIN join_team q ON t.seq = q.team_seq
WHERE
(
t.team_leader_seq = (
			SELECT seq
			FROM Users
			WHERE id = #{id}
		
)
OR
(
q.state = 'YES'
AND
q.user_seq = (
	SELECT seq
	FROM users
	WHERE id = #{id}
)
)
)
AND t.code = #{code}

mapper.update:
UPDATE team
SET start_Date = #{startDate}, end_Date = #{endDate} , description = #{description}
WHERE code = #{code}

UPDATE team
SET flag = 'NO'
WHERE code = #{code}
